/*Write a java program for Pyramid Example */
package assn2;

public class assn2_4 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<5;i++)
		{
			for(int j=0;j<=i;j++)
			{
				System.out.print("*  ");
			}
			System.out.println();
		}
	}
}
